        @res_hash = JSON.parse(res)
    end

    def self.find(str)
        findcharger = {}
        @res_hash["results"].each do |charger|
            if charger["address"]["city"].downcase == str.downcase || charger["address"]["zip"].downcase == str.downcase || charger["address"]["state"].downcase == str.downcase
                findcharger[charger["locationId"]] = charger
            end
        end
        findcharger
    end

    def self.all
        @res_hash["results"]
    end
